import React from 'react';

const SymptomsDropdown = ({ setState, scrollIntoView, actionProvider }) => {
    const symptoms = ["Fatigue", "Fever", "Loss of Appetite", "Coughing", "Sneezing"];

    const handleDropdownChange = (e) => {
        const selectedSymptom = e.target.value;
        // You can then use the actionProvider to handle the selected symptom
        actionProvider.handleSelectedSymptom(selectedSymptom);
        scrollIntoView();
    };

    return (
        <select onChange={handleDropdownChange} defaultValue="" style={{ width: '100%', padding: '10px', marginTop: '5px' }}>
            <option value="" disabled>Select a symptom</option>
            {symptoms.map(symptom => (
                <option key={symptom} value={symptom}>{symptom}</option>
            ))}
        </select>
    );
};

export default SymptomsDropdown;
