  const handleChange = (event) => {
    const selectedSymptom = event.target.value;
    
    actionProvider.handleSelectedSymptom(selectedSymptom);
  };

  return (
    <select onChange={handleChange} className='dropdown' >
      <option value="">Select a symptom</option>
      {symptoms.map((symptom, index) => (
        <option key={index} value={symptom}>{symptom}</option>
      ))}
    </select>
  );
};

export default SymptomsDropdown;
